    methods : {
        sendEducationRequest(){
            let data = {
                agreement : document.querySelector("input[name=education-agree]").value === 'on' ? 1 : 0 ,
                phone : document.querySelector("input[name=education-phone]").value,
                name : document.querySelector("input[name=education-name]").value,
                email : document.querySelector("input[name=education-email]").value,
                comment : document.querySelector("textarea[name=education-usertext]").value,
                url : window.location.href
            };
            this.$store.dispatch('education/send', data);
        }
    }
}
